/* eslint-disable */
import React, { Component } from 'react';
import ReactPullToRefresh from '../../modules/react-pull-to-refresh/src';
import ActivityIndicator from '../ActivityIndicator';

const arrowIcon = 'data:image/png;base64,';
const css = `.ptr-element .pull-down-icon {
  font-size: 34px;
  transition: all .25s ease;
  -webkit-transform: rotate(90deg);
  transform: rotate(90deg);
  margin-top: -22px;
  margin-left: auto;
  margin-right: auto;
  display: block;
  width: 22px;
  height: 22px;
}
.ptr-refresh .pull-down-icon {
  -webkit-transform: rotate(270deg);
  transform: rotate(270deg);
}
.ptr-loading .pull-down-icon {
  display: none;
}
.ptr-reset .pull-down-icon {
  display: block;
}

.ptr-element .loading {
  display: inline-block;
  display: none;
  margin-top: 5px;
}
.ptr-loading .loading {
  display: block;
  margin-bottom: 10px;
}`;

(function () {
  const styleTag = document.createElement('style');
  styleTag.innerHTML = css;
  document.getElementsByTagName('head')[0].appendChild(styleTag);
}());

class RefreshControl extends Component {

  static propTypes = {
    children: React.PropTypes.any,
    onRefresh: React.PropTypes.func.isRequired,
    refreshing: React.PropTypes.bool.isRequired,
    style: React.PropTypes.any
  }

  handleRefresh(resolve) {
    this.props.onRefresh();
    setInterval(() => {
      if (!this.props.refreshing) {
        resolve();
      }
    }, 1);
  }

  render() {
    return (
      <ReactPullToRefresh
        icon={<img width={22} height={22} src={arrowIcon} className='pull-down-icon' />}
        loading={<div className='loading'><ActivityIndicator animating /></div>}
        onRefresh={this.handleRefresh.bind(this)} >
        {this.props.children}
      </ReactPullToRefresh>
    );
  }
}

export default RefreshControl;
